"use client";
import { useEffect, useState } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { AnimatePresence, motion } from 'framer-motion';
import { SkipForward, Loader2 } from 'lucide-react';
import MessageList from '../../../components/Chat/MessageList';
import MessageInput from '../../../components/Chat/MessageInput';
import PresenceIndicator from '../../../components/Chat/PresenceIndicator';
import { Avatar } from '../../../components/ui/avatar';
import { MessageSkeleton } from '../../../components/ui/skeleton';
import { AuraBlendBackground } from '../../../components/InkAura';
import { useSessionStore } from '../../../stores/useSessionStore';
import { useWebRTC } from '../../../lib/hooks/useWebRTC';
import { supabase } from '../../../lib/supabase';
import type { ReplyMessage } from '../../../components/Chat/MessageReply';

export default function ChatRoomPage() {
  const params = useParams();
  const roomId = (params?.room as string) || 'room_demo';
  const session = useSessionStore((s) => s.session);
  const setSession = useSessionStore((s) => s.setSession);
  const [ready, setReady] = useState(false);
  const [showSkipConfirm, setShowSkipConfirm] = useState(false);
  const [skipping, setSkipping] = useState(false);
  const router = useRouter();

  const myId = session.userId || 'guest_local';
  const myAuraSeed = session.auraSeed ?? 42;
  const myRep = session.reputation ?? 50;

  // WebRTC P2P Hook
  const { messages, connectionState, partnerId, partnerTyping, sendMessage, sendTyping, editMessage, reactToMessage } = useWebRTC(roomId, myId);

  useEffect(() => {
    const initAuth = async () => {
      try {
        let userId = session.userId ?? null;
        if (!userId) {
          const { data } = await supabase.auth.getSession();
          userId = data?.session?.user?.id ?? null;
        }
        if (!userId) {
          const { data, error } = await supabase.auth.signInAnonymously();
          if (error) throw error;
          userId = data.user?.id ?? null;
          setSession({ ...session, userId });
        }
      } catch (_err) {
        // fallback to local session
      } finally {
        setReady(true);
      }
    };
    initAuth();
  }, [session, setSession]);

  const [panicked, setPanicked] = useState(false);

  // The Zen Panic Switch (Double tap Escape)
  useEffect(() => {
    let lastEsc = 0;
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        const now = Date.now();
        if (now - lastEsc < 500) {
          // Double tap detected
          setPanicked(true);
          document.title = "404 Not Found";
        }
        lastEsc = now;
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  if (panicked) {
    return (
      <div className="min-h-screen bg-white flex flex-col items-center justify-center p-8 font-mono text-black">
        <h1 className="text-4xl font-bold mb-4">404 Not Found</h1>
        <p>The requested URL was not found on this server.</p>
        <p className="mt-8 text-sm text-gray-500 hover:text-indigo-500 cursor-pointer transition-colors" onClick={() => { setPanicked(false); document.title = "InkHaven | Anonymous & Safe Chat"; }}>[Click to restore]</p>
      </div>
    );
  }

  const [myIntensity, setMyIntensity] = useState(0);

  const handleSkipChat = async () => {
    setShowSkipConfirm(false);
    setSkipping(true);
    try {
      const { data: sessionData } = await supabase.auth.getSession();
      const token = sessionData?.session?.access_token;

      // Sync chat history for authenticated users before leaving
      if (token && messages.length > 0) {
        try {
          fetch('/api/chat/sync', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ roomId, messages }),
            keepalive: true // Send even if component unmounts
          });
        } catch (e) {
          // ignore sync failure
        }
      }

      const res = await fetch('/api/quick-match', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(token ? { 'Authorization': `Bearer ${token}` } : {})
        },
        body: JSON.stringify({ action: 'skip', currentRoomId: roomId })
      });
      const data = await res.json();
      if (data.ok && data.data?.matchFound && data.data?.roomId) {
        router.push(`/chat/${data.data.roomId}`);
      } else {
        router.push('/quick-match');
      }
    } catch (err) {
      console.error('Skip chat failed:', err);
      setSkipping(false);
    }
  };

  if (!ready) {
    return (
      <div className="container mx-auto px-6 py-10">
        <div className="card p-0 overflow-hidden">
          <div className="px-6 py-4 border-b border-white/5">
            <div className="h-4 w-32 bg-white/5 rounded animate-pulse" />
          </div>
          <MessageSkeleton count={5} />
        </div>
      </div>
    );
  }

  const isConnected = connectionState === 'connected';

  return (
    <div className="container mx-auto px-6 py-8">
      <div className="hyper-glass overflow-hidden shadow-2xl border border-white/10">
        {/* Chat header */}
        <div className="flex items-center justify-between border-b border-white/5 bg-obsidian-900/40 backdrop-blur-md px-6 py-4">
          <div className="flex items-center gap-4">
            {isConnected ? (
              <Avatar
                userId={partnerId || 'partner'}
                displayName={'Connected Partner'}
                size="md"
                showStatus
                status={'online'}
              />
            ) : (
              <Avatar displayName="?" size="md" showStatus status={connectionState === 'connecting' ? 'away' : 'offline'} />
            )}
            <div>
              <div className="text-xl font-bold text-white tracking-wide">
                {isConnected ? 'Anonymous Partner' : 'Waiting in Limbo...'}
              </div>
              <div className="text-xs text-indigo-300 font-mono tracking-wider mt-0.5">
                {connectionState === 'connecting' ? 'NEGOTIATING P2P CONNECTION...' : connectionState === 'disconnected' ? 'PARTNER DISCONNECTED' : 'SECURE P2P CHANNEL'}
              </div>
            </div>
          </div>
          <div className="flex items-center gap-4">
            <PresenceIndicator
              onlineCount={isConnected ? 2 : 1}
              partnerStatus={isConnected ? 'online' : 'offline'}
              partnerName={'Partner'}
              isConnected={isConnected}
            />
            <button
              onClick={() => setShowSkipConfirm(true)}
              disabled={skipping}
              className="flex items-center gap-1.5 px-3 py-1.5 rounded-full bg-red-500/10 hover:bg-red-500/20 border border-red-500/20 text-red-400 text-xs font-medium transition-all hover:scale-105 active:scale-95 disabled:opacity-50 disabled:cursor-not-allowed"
              title="Skip to next chat"
            >
              <SkipForward size={14} />
              <span className="uppercase tracking-widest">Next</span>
            </button>
            <div className="pill border-indigo-500/30 text-indigo-400 bg-indigo-500/10 font-mono text-[10px] tracking-widest">P2P TUNNEL</div>
          </div>
        </div>

        {/* Chat area */}
        <AuraBlendBackground
          seed1={myAuraSeed}
          rep1={myRep}
          seed2={isConnected ? 777 : 0}
          rep2={50}
          intensity={myIntensity}
          className="relative flex flex-col h-[70vh]"
        >

          <MessageList
            roomId={roomId}
            myId={myId}
            messages={messages}
            partnerTyping={partnerTyping}
            onEdit={editMessage}
            onReact={reactToMessage}
          />

          <MessageInput
            myId={myId}
            onIntensityChange={setMyIntensity}
            onSendMessage={sendMessage}
            onTyping={sendTyping}
          />
        </AuraBlendBackground>

        {/* Skip confirmation dialog */}
        <AnimatePresence>
          {showSkipConfirm && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="absolute inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm"
              onClick={() => setShowSkipConfirm(false)}
            >
              <motion.div
                initial={{ scale: 0.9, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.9, opacity: 0 }}
                className="bg-slate-900 border border-white/10 rounded-2xl p-6 max-w-sm w-full mx-4 shadow-2xl"
                onClick={(e) => e.stopPropagation()}
              >
                <h3 className="text-lg font-semibold text-white mb-2">End this chat?</h3>
                <p className="text-sm text-white/50 mb-6">
                  This will end your current conversation and match you with a new person.
                </p>
                <div className="flex gap-3">
                  <button
                    onClick={() => setShowSkipConfirm(false)}
                    className="flex-1 px-4 py-2.5 rounded-xl bg-white/5 hover:bg-white/10 text-white/70 text-sm font-medium transition"
                  >
                    Stay
                  </button>
                  <button
                    onClick={handleSkipChat}
                    className="flex-1 px-4 py-2.5 rounded-xl bg-red-500/20 hover:bg-red-500/30 border border-red-500/30 text-red-400 text-sm font-medium transition flex items-center justify-center gap-2"
                  >
                    <SkipForward size={16} />
                    Next Chat
                  </button>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>

        {/* Searching overlay */}
        <AnimatePresence>
          {skipping && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="absolute inset-0 z-50 flex flex-col items-center justify-center bg-black/70 backdrop-blur-sm"
            >
              <Loader2 className="w-12 h-12 text-indigo-400 animate-spin mb-4" />
              <p className="text-lg font-medium text-white animate-pulse">Finding your next match...</p>
              <p className="text-sm text-white/40 mt-1">Hang tight</p>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}

'use client';

import React, { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { useSessionStore } from '../../stores/useSessionStore';
import { supabase } from '../../lib/supabase';
import { Button } from '../../components/ui/button';
import AuraSphere from '../../components/Profile/AuraSphere';
import { useToast } from '../../components/ui/toast';
import { Loader2, Zap, Users, ShieldCheck, Sparkles, Activity } from 'lucide-react';

export default function QuickMatchPage() {
  const router = useRouter();
  const { session } = useSessionStore();
  const [status, setStatus] = useState<'idle' | 'searching' | 'matched' | 'error'>('idle');
  const [errorMsg, setErrorMsg] = useState('');
  const toast = useToast();

  const auraSeed = session.auraSeed ?? 42;
  const reputation = session.reputation ?? 50;

  // 1. Auth Check
  useEffect(() => {
    const timer = setTimeout(() => {
      if (!session.userId) {
        router.push('/onboarding');
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [session, router]);

  // 2. Realtime Subscription for "Waiting" users
  useEffect(() => {
    if (status !== 'searching' || !session.userId) return;

    const channel = supabase
      .channel(`quick-match-${session.userId}`)
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'room_participants',
          filter: `user_id=eq.${session.userId}`
        },
        (payload) => {
          setStatus('matched');
          toast.success('Match found! Connecting you now...');
          setTimeout(() => {
            router.push(`/chat/${payload.new.room_id}`);
          }, 1500);
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [status, session.userId, router, toast]);

  const handleQuickMatch = async () => {
    try {
      setStatus('searching');
      setErrorMsg('');

      // Get auth token from Supabase session
      const { data: sessionData } = await supabase.auth.getSession();
      let token = sessionData?.session?.access_token ?? null;

      if (!token) {
        const { data: anonData, error: anonError } = await supabase.auth.signInAnonymously();
        if (anonError) throw anonError;
        token = anonData.session?.access_token ?? null;
        const userId = anonData.user?.id ?? session.userId;
        useSessionStore.getState().setSession({ ...session, userId });
      }

      if (!token) {
        throw new Error('Unable to authenticate. Please try refreshing the page.');
      }

      const res = await fetch('/api/quick-match', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({})
      });

      if (!res.ok) {
        const errorBody = await res.json().catch(() => ({}));
        throw new Error(errorBody.message || `Failed to join queue: ${res.statusText}`);
      }

      const data = await res.json();

      if (data.data.matchFound && data.data.roomId) {
        setStatus('matched');
        toast.success('Match found! Connecting you now...');
        router.push(`/chat/${data.data.roomId}`);
      }
    } catch (err: any) {
      console.error('Quick Match Error:', err);
      setStatus('error');
      setErrorMsg(err.message || 'Something went wrong. Please try again.');
      toast.error(err.message || 'Something went wrong.');
    }
  };

  if (!session.userId) {
    return <div className="min-h-screen flex items-center justify-center">
      <Loader2 className="w-8 h-8 animate-spin text-indigo-500" />
    </div>;
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 relative overflow-hidden">

      {/* Background aura glow */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-indigo-500/10 rounded-full blur-3xl animate-pulse" />
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-pulse" style={{ animationDelay: '1s' }} />
      </div>

      <div className="relative z-10 max-w-md w-full text-center space-y-8">

        {/* Header */}
        <div className="space-y-2">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent">
            Quick Match
          </h1>
          <p className="text-white/50">
            Instantly connect with someone available now.
          </p>
        </div>

        {/* Main Action Area */}
        <div className="py-12 flex flex-col items-center justify-center min-h-[400px]">
          {status === 'idle' && (
            <div className="relative group">
              <div className="absolute inset-0 bg-indigo-500/20 rounded-full blur-xl group-hover:bg-indigo-500/40 group-hover:blur-2xl transition-all duration-700 animate-pulse-glow" />
              <button
                onClick={handleQuickMatch}
                className="relative w-48 h-48 rounded-full bg-obsidian-900/80 backdrop-blur-xl border border-white/10 flex flex-col items-center justify-center shadow-glass-lg hover:scale-105 active:scale-95 transition-all duration-500 z-10 overflow-hidden"
              >
                {/* Inner button glow */}
                <div className="absolute inset-0 bg-gradient-to-b from-white/5 to-transparent" />
                <Zap className="w-16 h-16 text-indigo-400 mb-2 fill-current drop-shadow-[0_0_15px_rgba(99,102,241,0.5)]" />
                <span className="font-bold text-lg text-white tracking-wide">Enter The Void</span>
              </button>
            </div>
          )}

          {status === 'searching' && (
            <div className="flex flex-col items-center animate-in fade-in zoom-in duration-700">
              {/* Pulsing Aura during search */}
              <div className="relative mb-12">
                <AuraSphere inkId={session.inkId || 'fallback'} size="lg" isPulsing={true} comfortLevel={session.comfortLevel as any} />

                {/* Orbiting Search Ring */}
                <div className="absolute inset-[-20%] rounded-full border border-dashed border-indigo-500/50 animate-[spin_4s_linear_infinite]" />
                <div className="absolute inset-[-30%] rounded-full border border-purple-500/20 animate-[spin_8s_linear_infinite_reverse]" />
              </div>

              <div className="text-center">
                <p className="text-xl font-medium text-white tracking-widest uppercase flex items-center gap-3">
                  <Activity className="w-5 h-5 text-indigo-400 animate-pulse" />
                  Scanning Frequencies
                </p>
                <p className="text-sm text-white/40 mt-2 font-mono">Calibrating matching vectors...</p>
              </div>
            </div>
          )}

          {/* Vibe Check Limbo State */}
          {status === 'matched' && (
            <div className="flex flex-col items-center w-full animate-in slide-in-from-bottom-10 fade-in duration-1000">
              <div className="text-center mb-10">
                <p className="text-sm font-mono text-emerald-400 mb-2 tracking-widest uppercase">Signal Locked</p>
                <h2 className="text-3xl font-bold text-white">Vibe Check</h2>
                <p className="text-white/50 text-sm mt-2">A connection is forming. Prepare your energy.</p>
              </div>

              <div className="flex items-center justify-center gap-8 w-full">
                {/* User's Aura */}
                <div className="flex flex-col items-center">
                  <AuraSphere inkId={session.inkId || 'fallback'} size="md" />
                  <span className="text-xs text-white/40 mt-4 font-mono">YOU</span>
                </div>

                {/* The Spark / Connection Line */}
                <div className="relative flex-1 max-w-[100px] h-[2px] bg-white/10">
                  <div className="absolute inset-0 bg-gradient-to-r from-indigo-500 via-purple-500 to-emerald-500 animate-[shimmer_2s_infinite]" style={{ backgroundSize: '200% 100%' }} />
                  <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 bg-obsidian-900 border border-white/20 rounded-full p-2">
                    <Sparkles className="w-4 h-4 text-white" />
                  </div>
                </div>

                {/* Partner's Aura (Mysterious fallback since inkId isn't known until channel connects) */}
                <div className="flex flex-col items-center">
                  <AuraSphere inkId="mysterious_stranger" size="md" comfortLevel="bold" isPulsing={true} />
                  <span className="text-xs text-white/40 mt-4 font-mono tracking-widest">UNKNOWN</span>
                </div>
              </div>

              <div className="mt-12 bg-white/5 px-6 py-3 rounded-full border border-white/10 flex items-center gap-3 backdrop-blur-md">
                <Loader2 className="w-4 h-4 text-emerald-400 animate-spin" />
                <span className="text-sm text-white font-medium">Establishing Secure WebRTC Tunnel...</span>
              </div>
            </div>
          )}

          {status === 'error' && (
            <div className="text-center animate-in zoom-in-95 duration-300">
              <div className="bg-red-500/10 border border-red-500/20 p-6 rounded-2xl mb-6 backdrop-blur-md">
                <p className="text-red-400 font-medium mb-2">Connection Severed</p>
                <p className="text-sm text-white/50">{errorMsg}</p>
              </div>
              <Button onClick={() => setStatus('idle')} variant="secondary" className="px-8">Recalibrate & Retry</Button>
            </div>
          )}
        </div>

        {/* Features / Reassurance */}
        <div className="grid grid-cols-2 gap-4 max-w-sm mx-auto pt-8 border-t border-white/5">
          <div className="flex flex-col items-center glass-panel p-4">
            <ShieldCheck className="w-6 h-6 text-indigo-400 mb-2" />
            <span className="text-xs text-white/60 font-medium tracking-wide">ZERO DATA RETAINED</span>
          </div>
          <div className="flex flex-col items-center glass-panel p-4">
            <Users className="w-6 h-6 text-purple-400 mb-2" />
            <span className="text-xs text-white/60 font-medium tracking-wide">ANONYMOUS P2P</span>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';
import { useRef, useState } from 'react';
import { ReactSketchCanvas, ReactSketchCanvasRef } from 'react-sketch-canvas';
import { useToast } from '../ui/toast';
import { Button } from '../ui/button';
import { Eraser, SendHorizontal, X } from 'lucide-react';

interface GlowpadProps {
    onSend: (base64Image: string) => void;
    onCancel: () => void;
    color?: string;
}

export default function Glowpad({ onSend, onCancel, color = '#6366f1' }: GlowpadProps) {
    const canvasRef = useRef<ReactSketchCanvasRef>(null);
    const toast = useToast();
    const [isDrawing, setIsDrawing] = useState(false);

    const handleSend = async () => {
        if (!canvasRef.current) return;
        try {
            // Export as a lightweight PNG sequence via Data URI
            const dataUri = await canvasRef.current.exportImage('png');
            onSend(dataUri);
        } catch (err) {
            toast.error('Failed to process glow stroke.');
        }
    };

    const handleClear = () => {
        if (canvasRef.current) {
            canvasRef.current.clearCanvas();
            setIsDrawing(false);
        }
    };

    return (
        <div className="flex flex-col gap-3 p-4 bg-obsidian-900/80 backdrop-blur-2xl rounded-2xl border border-white/10 shadow-[0_-10px_40px_rgba(0,0,0,0.5)]">
            <div className="flex items-center justify-between px-2">
                <div className="text-sm font-semibold text-transparent bg-clip-text bg-gradient-to-r from-indigo-400 to-emerald-400 tracking-widest uppercase">
                    Ephemeral Canvas
                </div>
                <button onClick={onCancel} className="text-white/40 hover:text-white transition-colors">
                    <X size={18} />
                </button>
            </div>

            <div className="relative rounded-xl overflow-hidden border border-white/5 bg-[#0B0E14] shadow-inner" style={{ height: '200px' }}>
                {/* Ambient background grid */}
                <div className="absolute inset-0 opacity-10" style={{ backgroundImage: 'radial-gradient(circle at 1px 1px, white 1px, transparent 0)', backgroundSize: '20px 20px' }} />

                <div className="absolute inset-0 z-10" onPointerDown={() => setIsDrawing(true)}>
                    <ReactSketchCanvas
                        ref={canvasRef}
                        strokeWidth={4}
                        strokeColor={color}
                        canvasColor="transparent"
                        style={{ border: 'none' }}
                        withTimestamp={false}
                    />
                </div>
            </div>

            <div className="flex items-center justify-between">
                <button
                    onClick={handleClear}
                    className="flex items-center gap-2 px-3 py-1.5 rounded-lg text-xs font-medium text-white/50 hover:bg-white/5 hover:text-white transition-colors"
                >
                    <Eraser size={14} /> Clear
                </button>

                <Button
                    onClick={handleSend}
                    disabled={!isDrawing}
                    size="sm"
                    className="bg-indigo-500 hover:bg-indigo-400 text-white rounded-full px-5 flex items-center gap-2 shadow-[0_0_15px_rgba(99,102,241,0.4)] disabled:opacity-50 disabled:shadow-none transition-all"
                >
                    Shoot <SendHorizontal size={14} />
                </Button>
            </div>
        </div>
    );
}

/* eslint-env serviceworker */
// Clean, Minimal Service Worker for WebRTC Architecture
// This replaces the old database-heavy service worker and clears its queues.

const CACHE_NAME = 'inkhaven-chat-v3.0.0'

self.addEventListener('install', (event) => {
  self.skipWaiting();
});

self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('Service Worker: Clearing old cache', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    }).then(() => {
      // Clear the old IndexedDB queues that were causing 500/405 errors
      indexedDB.deleteDatabase('InkhavenOfflineDB');
      return self.clients.claim();
    })
  );
});

self.addEventListener('fetch', (event) => {
  // Pass through all requests directly to the network in the WebRTC era
  // No more caching API routes or queued database syncs.
  event.respondWith(fetch(event.request));
});
